mapper.update:
UPDATE FAQ_BOARD
   SET BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENTS = #{boardContents}
     , BOARD_MODIFY_DT = CURRENT_TIMESTAMP
 WHERE FAQ_NO = #{faqNo}

mapper.select:
SELECT COUNT(*)
  FROM FAQ_BOARD
 WHERE CATEGORY = ${category}

SELECT FAQ_NO, BOARD_TITLE, BOARD_CONTENTS, BOARD_CREATE_DT, BOARD_MODIFY_DT, HIT, CATEGORY, EMP_NO, EMP_NAME FROM ( SELECT ROW_NUMBER() OVER (ORDER BY FAQ_NO ${sort}) AS RN, FAQ.FAQ_NO, FAQ.BOARD_TITLE, FAQ.BOARD_CONTENTS, FAQ.BOARD_CREATE_DT, FAQ.BOARD_MODIFY_DT, FAQ.HIT, FAQ.CATEGORY, EMP.EMP_NO, EMP.EMP_NAME FROM EMPLOYEE EMP INNER JOIN FAQ_BOARD FAQ ON EMP.EMP_NO = FAQ.EMP_NO
CATEGORY = #{category} AND (FAQ.BOARD_TITLE LIKE '%' || #{query} || '%' OR FAQ.BOARD_CONTENTS LIKE '%' || #{query} || '%')
(FAQ.BOARD_TITLE LIKE '%' || #{query} || '%' OR FAQ.BOARD_CONTENTS LIKE '%' || #{query} || '%')
) WHERE RN BETWEEN #{begin} AND #{end}
